import uuid from "uuid";
import { GET_ITEMS, ADD_ITEMS, DELETE_ITEMS, ITEMS_LOADING } from "../actions/types";

const initialState = {
  items: [
    {
      id: uuid(),
      name: "egg"
    },
    {
      id: uuid(),
      name: "milk"
    },
    {
      id: uuid(),
      name: "steak"
    }
  ]
};

export default function(state = initialState, action) {
  switch (action.type) {
    case GET_ITEMS:
      return {
        ...state
      };
    case DELETE_ITEMS:
      return {
        ...state,
        items: state.items.filter(item => item.id !== action.DataId)
      };
    case ADD_ITEMS:
      return {
        ...state,
        items: [...state.items, action.item]
      };
case ITEMS_LOADING:
  return
    default:
      return state;
  }
}
