import { GET_ITEMS, ADD_ITEMS, DELETE_ITEMS, ITEMS_LOADING } from "./types";

export const getItems = () => {
  return {
    type: GET_ITEMS
  };
};

export const deleteItem = id => {
  return {
    type: DELETE_ITEMS,
    DataId: id
  };
};

export const addItem = name => {
  return {
    type: ADD_ITEMS,
    item: name
  };
};

export const setItemsLoading = () => {
  return {
    type: ITEMS_LOADING
  };
};
