import { GET_ERRORS, CLEAR_ERRORS } from "../actions/types";

const initialState = {
  mag: {},
  status: null,
  id: null
};

export default function(state = initialState, action) {
  switch (action.type) {
    case GET_ITEMS:
      return {
        mag: action.payload.msg,
        status: action.payload.status,
        id: action.payload.id
      };

    case CLEAR_ERRORS:
      return {
        msg: {},
        status: null,
        id: null
      };
  }
}
