import { Get_ERRORS, CLEAR_ERRORS } from "./";

//return errors

export const returnErrors = (msg, status, id = null) => {
  return {
    type: Get_ERRORS,
    payload: { msg, status, id }
  };
};

//Clear errors

export const clearErrors = () => {
  return {
    type: CLEAR_ERRORS
  };
};
