import uuid from "uuid";
import {
  GET_ITEMS,
  ADD_ITEMS,
  DELETE_ITEMS,
  ITEMS_LOADING
} from "../actions/types";

const initialState = {
  items: [],
  loading: false
};

export default function(state = initialState, action) {
  switch (action.type) {
    case GET_ITEMS:
      return {
        ...state,
        items: action.payload,
        loading: false
      };
    case DELETE_ITEMS:
      console.log(action.DataId);
      return {
        ...state,
        items: state.items.filter(item => item._id !== action.DataId)
      };
    case ADD_ITEMS:
      return {
        ...state,
        items: [...state.items, action.item]
      };
    case ITEMS_LOADING:
      return {
        ...state,
        loading: true
      };
    default:
      return state;
  }
}
